/*Largest product in a series

Find the thirteen adjacent digits in the 1000-digit number that have the greatest product. What is the value of this product?*/

let bigNum = "";
let bigArr = ('' + bigNum).split('').map(function(digit) {return +digit;});
let length = bigNum.length;

let bigProd = 0;
let depth = (bigArr.length - 13);

// Find the product of an array elements
function multip(array) {
    prod = 1;
    for (var i = 0; i < array.length; i++) {
    prod = prod * array[i];
    }
    return prod;
}

// Process the 1000 digit series
function sliceNum(bigarr) {
    for (var n = 0; n <= depth; n++) {
      var arslice = bigarr.slice(n,n+13);

      var prod = multip(arslice);

      if (prod > bigProd) { 
        bigProd = prod; 
        let slicestart = n;
        var topslice = bigarr.slice(slicestart, slicestart+13);
        topslice.push(n);   // add the start index to the end of the slice
      }
    }
    return topslice;
}

let topSlice = sliceNum(bigArr);
let slicestart = topSlice.pop();
let bigprod = multip(topSlice);

console.log(bigprod);